#!/bin/bash
docker buildx build --pull --no-cache --platform linux/amd64,linux/arm64 -t "$DOCKER_USER"/bananas:"$(date +"%Y%m%d")" -t "$DOCKER_USER"/bananas:latest --push -f ./bananas/Dockerfile.dev --cache-from type=local,src=/tmp/.buildx-cache/bananas --cache-to type=local,dest=/tmp/.buildx-cache-new/bananas ./bananas/